 private void setupContactAdminLink() {
        // Pastikan  punya JLabel bernama lblContactAdmin di mode Design
        if (lblContactAdmin != null) {
            lblContactAdmin.addMouseListener(new java.awt.event.MouseAdapter() {
                @Override
                public void mouseClicked(java.awt.event.MouseEvent evt) {
                    openWhatsApp();
                }

                @Override
                public void mouseEntered(java.awt.event.MouseEvent evt) {
                    // Mengubah kursor menjadi tangan saat mouse di atas label
                    lblContactAdmin.setCursor(new java.awt.Cursor(java.awt.Cursor.HAND_CURSOR));
                }

                @Override
                public void mouseExited(java.awt.event.MouseEvent evt) {
                    // Mengembalikan kursor ke normal saat mouse keluar
                    lblContactAdmin.setCursor(new java.awt.Cursor(java.awt.Cursor.DEFAULT_CURSOR));
                }
            });
        }
    }
 
 private void openWhatsApp() {
        try {
          
            String phoneNumber = "6289657531379"; 

           
            String message = "Halo Admin, saya butuh bantuan terkait aplikasi Sarpas.";
            String encodedMessage = java.net.URLEncoder.encode(message, "UTF-8");

           
            String url = "https://api.whatsapp.com/send/?phone=6289657531379&text&type=phone_number&app_absent=0" + phoneNumber + "?text=" + encodedMessage;

          
            if (Desktop.isDesktopSupported()) {
                Desktop.getDesktop().browse(new URI(url));
            } else {
                JOptionPane.showMessageDialog(this, "Fitur ini tidak didukung di sistem Anda.", "Error", JOptionPane.ERROR_MESSAGE);
            }

        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Gagal membuka WhatsApp. Pastikan Anda terhubung ke internet.", "Error", JOptionPane.ERROR_MESSAGE);
            e.printStackTrace(); 
        }
    }
 
 private void setLogoIcon() {
    

        // 1. Pecah string Base64 Anda yang sangat panjang menjadi beberapa bagian.
        //    Anda bisa memecahnya di mana saja.
        String logoBase64_part1 = "data:image/png;base64,";
        String logoBase64_part2 =  "";
        String logoBase64_part3 = "";
        String logoBase64_part4 ="";
//        String logoBase64_part2 = "zxcvbnm... (bagian kedua dari teks Base64 Anda)";
//        String logoBase64_part3 = "qwerty... (dan seterusnya jika perlu)";

        // 2. Gabungkan kembali semua bagian tersebut.
        String fullLogoBase64 = logoBase64_part1 + logoBase64_part2 + logoBase64_part3 + logoBase64_part4;

        // 3. Gunakan string yang sudah digabung untuk membuat ikon.
        //    Pastikan Anda punya JLabel bernama 'lblLogo' di mode Design.
        //    Ubah angka 150, 150 sesuai ukuran logo yang Anda inginkan.
        if (lbllogo != null) {
            lbllogo.setIcon(ImageUtil.getIconFromBase64(fullLogoBase64, 100, 100));
        }
    }
 
